and column_id= #{columnId,jdbcType=INTEGER}
and column_id in (1,2)
and create_time between #{createTimeStart,jdbcType=TIMESTAMP,typeHandler=com.chulung.mybatis.typehandler.LocalDateTimeTypeHandler} and #{createTimeEnd,jdbcType=TIMESTAMP,typeHandler=com.chulung.mybatis.typehandler.LocalDateTimeTypeHandler}
and id in
#{item}
and is_delete ='N' order by id desc


SELECT * from article a 
where a.id NOT IN(
	SELECT article_id from meta_cl_blog_log m,article a 
	where a.id=m.article_id and a.update_time < m.lastest_post_time and site=#{siteName}
